"use strict";
var _path = require("path");
var _util = require("../util");
describe("getDest", ()=>{
    it("does not modify the filename by default", ()=>{
        expect((0, _util).getDest((0, _path).join(process.cwd(), "src/path/name.ts"), "foo/bar", false)).toEqual((0, _path).join("foo", "bar", "src", "path", "name.ts"));
    });
    it("when stripLeadingPaths is true, it removes leading paths", ()=>{
        expect((0, _util).getDest((0, _path).join(process.cwd(), "src/path/name.ts"), "foo/bar", true)).toEqual((0, _path).join("foo", "bar", "path", "name.ts"));
    });
    it("when stripLeadingPaths is true, it also resolves relative paths", ()=>{
        expect((0, _util).getDest((0, _path).join(process.cwd(), "../../path/name.ts"), "foo/bar", true)).toEqual((0, _path).join("foo", "bar", "path", "name.ts"));
    });
});
